import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Aside from "../Aside/";

const Layout = () => {
  const [asideHeight, setAsideHeight] = useState(0);
  const handleAsideHeight = (height) => {
    setAsideHeight(height);
  };

  return (
    <>
      <Aside onAsideHeight={handleAsideHeight} />
      <main className="container" style={{ marginTop: asideHeight }}>
        <Outlet />
      </main>
    </>
  );
};

export { Layout };
