import React, { useEffect, useRef } from 'react';
import AStyle from "../../styles/aside.module.css";

const Aside = ({ onAsideHeight }) => {
  const asideRef = useRef(null);

  useEffect(() => {
    if (asideRef.current) {
      const height = asideRef.current.clientHeight;
      onAsideHeight(height);
    }
  }, [onAsideHeight]);

  return (
    <aside className={AStyle.aside} ref={asideRef}>
      aside
    </aside>
  );
};

export default Aside;
